src/index.css:
:root {
    --primary: #4f46e5;
    --primary-hover: #4338ca;
    --secondary: #64748b;
    --background: #f1f5f9;
    --surface: #ffffff;
    --text-primary: #0f172a;
    --text-secondary: #64748b;
    --border: #e2e8f0;
    --success: #10b981;
    --warning: #f59e0b;
    --danger: #ef4444;
    --sidebar-bg: #1e293b;
    --sidebar-text: #94a3b8;
    --sidebar-text-active: #ffffff;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Inter', system-ui, -apple-system, sans-serif;
    background-color: var(--background);
    color: var(--text-primary);
    -webkit-font-smoothing: antialiased;
}

a {
    text-decoration: none;
    color: inherit;
}

button {
    cursor: pointer;
    border: none;
    background: none;
    font-family: inherit;
}

src/components/Sidebar.module.css:
.sidebar {
    width: 260px;
    background-color: var(--sidebar-bg);
    color: var(--sidebar-text);
    display: flex;
    flex-direction: column;
    height: 100vh;
    position: fixed;
    left: 0;
    top: 0;
    z-index: 50;
    transition: transform 0.3s ease;
}

.header {
    padding: 24px;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.logo {
    display: flex;
    align-items: center;
    gap: 12px;
    font-size: 1.25rem;
    font-weight: 700;
    color: #fff;
}

.nav {
    flex: 1;
    padding: 24px 0;
    overflow-y: auto;
}

.nav ul {
    list-style: none;
}

.nav a {
    display: flex;
    align-items: center;
    gap: 12px;
    padding: 12px 24px;
    color: var(--sidebar-text);
    transition: all 0.2s;
    font-weight: 500;
}

.nav a:hover,
.nav a.active {
    color: var(--sidebar-text-active);
    background-color: rgba(255, 255, 255, 0.05);
    border-right: 3px solid var(--primary);
}

.footer {
    padding: 24px;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
}

.footer a {
    display: flex;
    align-items: center;
    gap: 12px;
    color: var(--sidebar-text);
    transition: color 0.2s;
}

.footer a:hover {
    color: #fff;
}

@media (max-width: 768px) {
    .sidebar {
        transform: translateX(-100%);
    }

    .sidebar.open {
        transform: translateX(0);
    }
}

src/components/Topbar.module.css:
.topbar {
    height: 70px;
    background-color: var(--surface);
    border-bottom: 1px solid var(--border);
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0 32px;
    position: sticky;
    top: 0;
    z-index: 40;
}

.toggle {
    display: none;
    cursor: pointer;
    color: var(--text-secondary);
}

.search {
    display: flex;
    align-items: center;
    background-color: var(--background);
    padding: 8px 16px;
    border-radius: 8px;
    width: 300px;
}

.searchIcon {
    color: var(--text-secondary);
    margin-right: 8px;
}

.search input {
    border: none;
    background: transparent;
    outline: none;
    width: 100%;
    color: var(--text-primary);
    font-family: inherit;
}

.profile {
    display: flex;
    align-items: center;
    gap: 24px;
}

.notifications {
    position: relative;
    cursor: pointer;
    color: var(--text-secondary);
}

.badge {
    position: absolute;
    top: -2px;
    right: -2px;
    width: 8px;
    height: 8px;
    background-color: var(--danger);
    border-radius: 50%;
    border: 2px solid var(--surface);
}

.userInfo {
    display: flex;
    align-items: center;
    gap: 12px;
    cursor: pointer;
}

.userInfo img {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    object-fit: cover;
}

.userInfo span {
    font-weight: 500;
    color: var(--text-primary);
}

@media (max-width: 768px) {
    .toggle {
        display: block;
    }

    .search {
        display: none;
    }

    .userInfo span {
        display: none;
    }
}

