import React, { useState } from 'react'
import Sidebar from './Sidebar'
import Topbar from './Topbar'
import styles from './MainLayout.module.css'

const MainLayout = ({ children }) => {
    const [sidebarOpen, setSidebarOpen] = useState(false)

    return (
        <div className={styles.container}>
            <div className={`${styles.sidebarWrapper} ${sidebarOpen ? styles.open : ''}`}>
                <Sidebar />
            </div>

            {/* Overlay for mobile */}
            {sidebarOpen && (
                <div
                    className={styles.overlay}
                    onClick={() => setSidebarOpen(false)}
                />
            )}

            <div className={styles.mainContent}>
                <Topbar onMenuClick={() => setSidebarOpen(true)} />
                <main className={styles.content}>
                    {children}
                </main>
            </div>
        </div>
    )
}

export default MainLayout

src/components/MainLayout.module.css:
.container {
    display: flex;
    min-height: 100vh;
}

.sidebarWrapper {
    width: 260px;
    flex-shrink: 0;
}

.mainContent {
    flex: 1;
    display: flex;
    flex-direction: column;
    min-width: 0;
    /* Prevent flex child overflow */
}

.content {
    padding: 32px;
    flex: 1;
}

.overlay {
    display: none;
}

@media (max-width: 768px) {
    .sidebarWrapper {
        width: 0;
        position: fixed;
        z-index: 50;
    }

    .sidebarWrapper :global(.sidebar) {
        transform: translateX(-100%);
    }

    .sidebarWrapper.open :global(.sidebar) {
        transform: translateX(0);
    }

    .overlay {
        display: block;
        position: fixed;
        inset: 0;
        background-color: rgba(0, 0, 0, 0.5);
        z-index: 45;
    }
}

src/components/StatsCards.module.css:
.grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
    gap: 24px;
}

.card {
    background-color: var(--surface);
    padding: 24px;
    border-radius: 16px;
    box-shadow: 0 1px 2px 0 rgb(0 0 0 / 0.05);
    display: flex;
    align-items: flex-start;
    gap: 20px;
    transition: transform 0.2s, box-shadow 0.2s;
}

.card:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1);
}

.iconWrapper {
    width: 48px;
    height: 48px;
    border-radius: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.iconWrapper.blue {
    background-color: #e0e7ff;
    color: #4f46e5;
}

.iconWrapper.green {
    background-color: #dcfce7;
    color: #10b981;
}

.iconWrapper.yellow {
    background-color: #fef3c7;
    color: #f59e0b;
}

.info h3 {
    font-size: 0.875rem;
    font-weight: 500;
    color: var(--text-secondary);
    margin-bottom: 4px;
}

.value {
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--text-primary);
    margin-bottom: 4px;
}

.trend {
    font-size: 0.875rem;
    font-weight: 500;
    display: flex;
    align-items: center;
    gap: 4px;
}

.trend.up {
    color: var(--success);
}

.trend.down {
    color: var(--danger);
}

